mapper.select:
SELECT DISTINCT b.*, c.id as cat_id, c.name as cat_name, c.color as cat_color, c.icon as cat_icon, u.nickname as uploader_nickname FROM block b LEFT JOIN sound_category c ON b.category_id = c.id LEFT JOIN users u ON b.uploader_id = u.id
INNER JOIN block_tag bt ON b.id = bt.block_id
b.is_active = true
AND b.status = #{status}
AND b.category_id = #{categoryId}
AND bt.tag_id IN
#{tagId}
AND b.title ILIKE CONCAT('%', #{keyword}, '%')
ORDER BY b.play_count DESC, b.created_at DESC
ORDER BY b.created_at DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(DISTINCT b.id)
FROM block b

    INNER JOIN block_tag bt ON b.id = bt.block_id


    b.is_active = true

        AND b.status = #{status}


        AND b.category_id = #{categoryId}


        AND bt.tag_id IN

            #{tagId}



        AND b.title ILIKE CONCAT('%', #{keyword}, '%')
